        self.token.set_token_uri(token_id, metadata_url)?;
        Ok(token_id)
    }

    fn transfer(from: Address, to: Address, token_id: u256) -> Result<(), Error> {
        self.token.transfer(from, to, token_id)
    }

    fn burn(token_id: u256) -> Result<(), Error> {
        self.token.burn(token_id)
    }

