      model: 'gemini-2.0-flash-001',
      temperature: 0,
      authOptions: {
        credentials: vertexProviderSettings.googleAuthOptions.credentials,
        projectId: vertexProviderSettings.project!
      },
      location: vertexProviderSettings.location
    })
