    "version": 2,
    "waiters": {
        "notebook_execution_terminal": {
            "operation": "DescribeNotebookExecution",
            "delay": 30,
            "maxAttempts": 120,
            "acceptors": [
                {
                    "matcher": "path",
                    "argument": "NotebookExecution.Status",
                    "expected": "FINISHED",
                    "state": "success"
                },
                {
                    "matcher": "path",
                    "argument": "NotebookExecution.Status",
                    "expected": "FAILED",
                    "state": "failure"
                },

            ]
        }
    }
}

