				"to": 330
			},
			"id": 2342,
			"p": 0.641946,
			"t_dtw": -1
		},
		{
			"text": " out",
			"timestamps": {
				"from": "00:00:00,330",
				"to": "00:00:00,530"
			},
			"offsets": {
				"from": 330,
				"to": 530
			},
			"id": 503,
			"p": 0.99322,
			"t_dtw": -1
		},
		{
